import { useEffect, useState } from "react";
import { getActiveProducts } from "../api/products";
import ProductForm from "../components/ProductForm";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [showForm, setShowForm] = useState(false);

  const loadProducts = async () => {
    const data = await getActiveProducts();
    setProducts(data);
  };

  useEffect(() => {
    loadProducts();
  }, []);

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">üõçÔ∏è Productos Activos</h1>

      <button
        onClick={() => setShowForm(!showForm)}
        className="bg-blue-600 text-white px-4 py-2 rounded-md mb-4"
      >
        {showForm ? "Cerrar" : "Agregar Producto"}
      </button>

      {showForm && <ProductForm onProductCreated={loadProducts} />}

      <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
  {products.length > 0 ? (
    products.map((p) => (
      <div
        key={p.id}
        className="bg-white border border-gray-200 rounded-2xl shadow-sm hover:shadow-xl transition-all duration-300 overflow-hidden"
      >
        {/* Imagen temporal o placeholder */}
        <div className="bg-gradient-to-r from-blue-500 to-indigo-500 h-36 flex items-center justify-center text-white text-3xl font-bold">
          {p.name.charAt(0).toUpperCase()}
        </div>

        {/* Contenido del producto */}
        <div className="p-5">
          <h2 className="text-lg font-semibold text-gray-900 mb-1 truncate">
            {p.name}
          </h2>
          <p className="text-gray-600 text-sm mb-3 line-clamp-2">
            {p.description || "Sin descripci√≥n"}
          </p>

          <div className="flex items-center justify-between mt-3">
            <span className="text-2xl font-bold text-green-600">
              ${p.price}
            </span>
            <span
              className={`px-3 py-1 text-xs font-medium rounded-full ${
                p.stock > 0
                  ? "bg-green-100 text-green-700"
                  : "bg-red-100 text-red-700"
              }`}
            >
              {p.stock > 0 ? `Stock: ${p.stock}` : "Sin stock"}
            </span>
          </div>
        </div>
      </div>
    ))
  ) : (
    <p className="text-gray-500 col-span-full text-center">
      No hay productos activos a√∫n.
    </p>
  )}
</div>

    </div>
  );
};

export default Products;
